import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Ensure you have this line to include the CSS styles

function FileUpload() {
    const [selectedFile, setSelectedFile] = useState(null);
    const [dataFrame, setDataFrame] = useState([]); // Initialize as an empty array

    const handleFileSelect = (event) => {
        setSelectedFile(event.target.files[0]);
    }

    useEffect(() => { // This useEffect will log every time dataFrame changes.
        console.log('DataFrame state:', dataFrame);
    }, [dataFrame]);

    const handleUpload = async () => {
        const formData = new FormData();
        formData.append('file', selectedFile);

        try {
            const response = await axios.post('/upload/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            console.log('Raw response:', response.data);
            setDataFrame(response.data); 
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    };

    return (
    <div className="App">
      <div className="content-wrapper">
        <input type="file" onChange={handleFileSelect} />
        <button onClick={handleUpload}>Upload</button>
        {dataFrame.length === 0 && <p>No data to display</p>}
      </div>
      {dataFrame.length > 0 && (
        <div className="table-wrapper">
          <table>
            <thead>
              <tr>
                {/* Table headers */}
              </tr>
            </thead>
            <tbody>
              {/* Table body */}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default FileUpload;
