import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css'; // Ensure you have this line to include the CSS styles

function FileUpload() {
    const [selectedFile, setSelectedFile] = useState(null);
    const [dataFrame, setDataFrame] = useState([]); // Initialize as an empty array

    const handleFileSelect = (event) => {
        setSelectedFile(event.target.files[0]);
    }

    useEffect(() => { // This useEffect will log every time dataFrame changes.
        console.log('DataFrame state:', dataFrame);
    }, [dataFrame]);

    const handleUpload = async () => {
        const formData = new FormData();
        formData.append('file', selectedFile);

        try {
            const response = await axios.post('/upload/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            console.log('Raw response:', response.data);
            setDataFrame(response.data); 
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    };

    return (
        <div className="App"> {/* Use the "App" class to apply your styles */}
            <div className="content-wrapper"> {/* This div will center the file input and button */}
                <input type="file" onChange={handleFileSelect} />
                <button onClick={handleUpload}>Upload</button>
                <p>{dataFrame.length === 0 && "No data to display"}</p>
            </div>
            {dataFrame.length > 0 && (
                <div className="table-wrapper"> {/* This div is for styling your table */}
                    <table>
                        <thead>
                            <tr>
                                {/* Create table headers dynamically from the first row keys */}
                                {Object.keys(dataFrame[0]).map((key, index) => (
                                    <th key={index}>{key}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {/* Create table rows dynamically */}
                            {dataFrame.map((row, rowIndex) => (
                                <tr key={rowIndex}>
                                    {Object.values(row).map((value, valueIndex) => (
                                        <td key={valueIndex}>{value !== null ? value : "N/A"}</td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default FileUpload;
